mapper.select:
select * from buy
where 1=1

    and MATER_ID = #{mater_id}

mapper.update:
update buy
set mater_name = #{mater_name}
,buying = #{buying}
,quantity = #{quantity}
,unit = #{unit}
,amount = #{amount}
,order_date = #{order_date}
,emp_name = #{emp_name}
,update_date = sysdate
where MATER_ID = #{mater_id}

mapper.select:
select * from buy

    
        AND content = #{map.C}
    
    
        AND title = #{map.T}
